CREATE TABLE `consultorio`.`deudas` (`id` INT NOT NULL AUTO_INCREMENT , `patient_id` INT NOT NULL , `motivo` INT NOT NULL , `user_id` INT NOT NULL , `registro` DATETIME NULL DEFAULT CURRENT_TIMESTAMP , `fecha` DATE NOT NULL , `monto` FLOAT NOT NULL , `activo` INT NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `consultorio`.`membresias` (`id` INT NOT NULL AUTO_INCREMENT , `patient_id` INT NOT NULL , `inicio` INT NOT NULL , `fin` INT NOT NULL , `tipo` INT NOT NULL COMMENT '15=kurame, 28=clinica' , `user_id` INT NOT NULL , `cuotas` INT NOT NULL , `monto` FLOAT NOT NULL , `activo` INT NULL DEFAULT '1' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
