ALTER TABLE `tipo_pagos` CHANGE `descripcion` `descripcion` VARCHAR(250) NOT NULL;
ALTER TABLE `extra_payments` CHANGE `type` `type` TINYINT(4) NULL DEFAULT NULL COMMENT 'tabla tipo_pagos';
